{% set start = frappe.form_dict.get("p", 1) %}
{% set start = start|int %}

{% set page_length = 20 %}

{% set total_items = frappe.db.count("Events", filters={"user": current_user.name}) %}

{% set pages_required = (total_items / page_length)|round|int %}

{% if pages_required <= 0 %}
{% set pages_required = 1 %}
{% endif %}


{% if start <= 0 %}
    {% set start = 1 %}    
{% endif %}

{% if start > pages_required %}
    {% set start = pages_required %}    
{% endif %}


{% set db_start = start - 1 %}
{% set actions = frappe.db.get_list("Events", filters={"user": current_user.name},
fields=["name", "title", "description", "creation", "location"], ignore_permissions=1
, page_length=page_length, start=db_start * page_length, order_by="creation desc") %}

{% for action in actions %}
{% set exists = frappe.db.exists("Location", action.location) %}

{% set city = None %}
{% set state = None %}
{% set district = None %}
{% if exists %}
    {% set city, state, district = frappe.db.get_value("Location", action.location, ["city", "state", "district"]) %}
{% endif %}
<div class="profile-tab-heading">
    {{action.title}}
    {% if frappe.session.user == current_user.name and frappe.session.user != "Guest" %}
        <a style="font-size:12px;" href="/edit-actions/{{action.name}}/edit">Edit</a>
    {% endif %}
    <span class="samaaja-action-meta small text-muted">&nbsp;{{frappe.utils.pretty_date(action.creation)}}
    {% if city or state %}
        in
        {% if city %}
        {{-city-}},
        {% endif %}

        {% if district %}
        {{-district-}},
        {% endif %}

        {% if state %}
        {{-state-}}
        {% endif %}
    {% endif %}
    </span>
</div>
<div class="profile-tab-description">
    {{-action.description-}} 
</div>
{% endfor %}

{% if actions|length == 0 %}
<p>No actions found. </p>
{% endif %}

<div class="text-center">
    <div class="pagination">
    {% if start > 1 %}
        <a href="/users/{{current_user.username}}?p={{start-1}}">&laquo;</a>
    {% for i in range(pages_required) %} 
        <a class="{% if start == i + 1 %}active{%endif%}" href="/users/{{current_user.username}}?p={{i+1}}">{{i+1}}</a>
    {% endfor %}
    {% endif %}
    {% if start < pages_required %}
        <a href="/users/{{current_user.username}}?p={{start+1}}">&raquo;</a>
    {% endif %}
    </div>
</div>
